/**/
// Dark/Light Mode Toggle
const themeToggle = document.getElementById('themeToggle');
const body = document.body;

// Check for saved theme preference or default to dark
const currentTheme = localStorage.getItem('theme') || 'dark';
body.setAttribute('data-theme', currentTheme === 'light' ? 'light' : '');
themeToggle.textContent = currentTheme === 'light' ? '☀️' : '🌙';

themeToggle.addEventListener('click', () => {
  const currentTheme = body.getAttribute('data-theme');
  const newTheme = currentTheme === 'light' ? '' : 'light';

  body.setAttribute('data-theme', newTheme);
  localStorage.setItem('theme', newTheme === 'light' ? 'light' : 'dark');
  themeToggle.textContent = newTheme === 'light' ? '☀️' : '🌙';
});

// Copy post URL function
window.copyPostUrl = function(url) {
  navigator.clipboard.writeText(url).then(() => {
    // Could show a toast notification
    alert("Link copied to clipboard!");
  });
};

// User Account Management
const userAccountBtn = document.getElementById('userAccountBtn');
const userAccountModal = document.getElementById('userAccountModal');
const closeModal = document.getElementById('closeModal');
const loginForm = document.getElementById('loginForm');
const userProfile = document.getElementById('userProfile');
const loginBtn = document.getElementById('loginBtn');
const registerBtn = document.getElementById('registerBtn');
const logoutBtn = document.getElementById('logoutBtn');
const usernameInput = document.getElementById('usernameInput');
const passwordInput = document.getElementById('passwordInput');

// User preferences checkboxes
const saveToolHistory = document.getElementById('saveToolHistory');
const enableNotifications = document.getElementById('enableNotifications');
const autoSaveDrafts = document.getElementById('autoSaveDrafts');

// User info elements
const welcomeUser = document.getElementById('welcomeUser');
const toolsUsedCount = document.getElementById('toolsUsedCount');
const accountCreated = document.getElementById('accountCreated');

// Modal controls
userAccountBtn.addEventListener('click', () => {
  userAccountModal.style.display = 'flex';
  checkLoginStatus();
});

closeModal.addEventListener('click', () => {
  userAccountModal.style.display = 'none';
});

userAccountModal.addEventListener('click', (e) => {
  if (e.target === userAccountModal) {
    userAccountModal.style.display = 'none';
  }
});

// Login functionality
loginBtn.addEventListener('click', () => {
  const username = usernameInput.value.trim();
  const password = passwordInput.value.trim();

  if (!username || !password) {
    alert("Please enter both username and password");
    return;
  }

  const users = JSON.parse(localStorage.getItem('toolUsers') || '{}');

  if (users[username] && users[username].password === password) {
    localStorage.setItem('currentUser', username);
    alert("Login successful!");
    checkLoginStatus();
    loadUserPreferences();
    updateUserStats();
  } else {
    alert("Invalid username or password");
  }
});

// Register functionality
registerBtn.addEventListener('click', () => {
  const username = usernameInput.value.trim();
  const password = passwordInput.value.trim();

  if (!username || !password) {
    alert("Please enter both username and password");
    return;
  }

  if (password.length < 6) {
    alert("Password must be at least 6 characters long");
    return;
  }

  const users = JSON.parse(localStorage.getItem('toolUsers') || '{}');

  if (users[username]) {
    alert("Username already exists");
    return;
  }

  users[username] = {
    password: password,
    created: new Date().toISOString(),
    preferences: {
      saveToolHistory: true,
      enableNotifications: true,
      autoSaveDrafts: false
    },
    stats: {
      toolsUsed: 0
    }
  };

  localStorage.setItem('toolUsers', JSON.stringify(users));
  localStorage.setItem('currentUser', username);
  alert("Registration successful!");
  checkLoginStatus();
  loadUserPreferences();
  updateUserStats();
});

// Logout functionality
logoutBtn.addEventListener('click', () => {
  localStorage.removeItem('currentUser');
  alert("Logged out successfully!");
  checkLoginStatus();
});

// Check login status
function checkLoginStatus() {
  const currentUser = localStorage.getItem('currentUser');
  const users = JSON.parse(localStorage.getItem('toolUsers') || '{}');

  if (currentUser && users[currentUser]) {
    loginForm.style.display = 'none';
    userProfile.style.display = 'block';
    welcomeUser.textContent = "Welcome, " + currentUser + "!";
  } else {
    loginForm.style.display = 'block';
    userProfile.style.display = 'none';
    welcomeUser.textContent = "Welcome, Guest!";
    usernameInput.value = '';
    passwordInput.value = '';
  }
}

// Load user preferences
function loadUserPreferences() {
  const currentUser = localStorage.getItem('currentUser');
  const users = JSON.parse(localStorage.getItem('toolUsers') || '{}');

  if (currentUser && users[currentUser]) {
    const prefs = users[currentUser].preferences;
    saveToolHistory.checked = prefs.saveToolHistory;
    enableNotifications.checked = prefs.enableNotifications;
    autoSaveDrafts.checked = prefs.autoSaveDrafts;
  }
}

// Save user preferences when changed
[saveToolHistory, enableNotifications, autoSaveDrafts].forEach(checkbox => {
  checkbox.addEventListener('change', () => {
    const currentUser = localStorage.getItem('currentUser');
    const users = JSON.parse(localStorage.getItem('toolUsers') || '{}');

    if (currentUser && users[currentUser]) {
      users[currentUser].preferences = {
        saveToolHistory: saveToolHistory.checked,
        enableNotifications: enableNotifications.checked,
        autoSaveDrafts: autoSaveDrafts.checked
      };
      localStorage.setItem('toolUsers', JSON.stringify(users));
    }
  });
});

// Update user statistics
function updateUserStats() {
  const currentUser = localStorage.getItem('currentUser');
  const users = JSON.parse(localStorage.getItem('toolUsers') || '{}');

  if (currentUser && users[currentUser]) {
    const userData = users[currentUser];
    toolsUsedCount.textContent = userData.stats.toolsUsed || 0;
    accountCreated.textContent = new Date(userData.created).toLocaleDateString();
  }
}

// Track tool usage (to be called from tool pages)
window.trackToolUsage = function(toolName) {
  const currentUser = localStorage.getItem('currentUser');
  const users = JSON.parse(localStorage.getItem('toolUsers') || '{}');

  if (currentUser && users[currentUser] && users[currentUser].preferences.saveToolHistory) {
    users[currentUser].stats.toolsUsed = (users[currentUser].stats.toolsUsed || 0) + 1;

    // Store tool usage history
    const history = users[currentUser].toolHistory || [];
    history.push({
      tool: toolName,
      timestamp: new Date().toISOString()
    });

    // Keep only last 100 entries
    if (history.length > 100) {
      history.shift();
    }

    users[currentUser].toolHistory = history;
    localStorage.setItem('toolUsers', JSON.stringify(users));
  }
};

// Load custom settings from localStorage
function loadCustomSettings() {
  const settings = localStorage.getItem('portfolioSettings');
  if (settings) {
    const data = JSON.parse(settings);

    // Apply theme colors
    if (data.primaryColor) {
      document.documentElement.style.setProperty('--accent', data.primaryColor);
    }
    if (data.secondaryColor) {
      // Could add secondary color variable
    }

    // Apply default theme
    if (data.defaultTheme === 'light') {
      body.setAttribute('data-theme', 'light');
      themeToggle.textContent = '☀️';
      localStorage.setItem('theme', 'light');
    }

    // Apply custom text (would need to modify HTML elements)
    // This is limited in Blogger templates, but could work for some elements
  }
}

// PWA Service Worker Registration (Inline for Blogger compatibility)
if ('serviceWorker' in navigator) {
  window.addEventListener('load', function() {
    // Register inline service worker for Blogger
    navigator.serviceWorker.register('/sw.js', { scope: '/' }).then(function(registration) {
      console.log('ServiceWorker registration successful with scope: ', registration.scope);
    }).catch(function(err) {
      console.log('ServiceWorker registration failed: ', err);
      // Fallback: try to register from alternative location if available
      if (window.pwaConfig && window.pwaConfig.swUrl) {
        navigator.serviceWorker.register(window.pwaConfig.swUrl).then(function(registration) {
          console.log('ServiceWorker registered from alternative URL');
        }).catch(function(err) {
          console.log('Alternative ServiceWorker registration also failed');
        });
      }
    });
  });
}

// PWA Install Prompt
let deferredPrompt;
const installBtn = document.createElement('button');
installBtn.id = 'installBtn';
installBtn.innerHTML = '📱 Install App';
installBtn.style.cssText = 'position: fixed; bottom: 20px; right: 20px; background: var(--accent); color: white; border: none; padding: 12px 20px; border-radius: 25px; cursor: pointer; font-weight: 600; display: none; z-index: 1000; box-shadow: 0 4px 16px rgba(0,0,0,0.2);';

window.addEventListener('beforeinstallprompt', function(e) {
  e.preventDefault();
  deferredPrompt = e;
  installBtn.style.display = 'block';

  installBtn.addEventListener('click', function() {
    installBtn.style.display = 'none';
    deferredPrompt.prompt();
    deferredPrompt.userChoice.then(function(choiceResult) {
      if (choiceResult.outcome === 'accepted') {
        console.log('User accepted the install prompt');
      } else {
        console.log('User dismissed the install prompt');
      }
      deferredPrompt = null;
    });
  });
});

window.addEventListener('appinstalled', function(evt) {
  console.log('App was installed successfully');
  installBtn.style.display = 'none';
});

// Add install button to page
document.addEventListener('DOMContentLoaded', function() {
  document.body.appendChild(installBtn);
});

// Initialize on page load
loadCustomSettings();
checkLoginStatus();
loadUserPreferences();
updateUserStats();
